function checkRuralOrIndustrialHome(){var e=document.querySelectorAll(".page-template-home .btn--red");console.log(e),e.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();var t=e.target.getAttribute("data-attr");sessionStorage.setItem("sitedirectory",t),console.log("sitedirectory set to: "+t),window.location.href=e.target.href})})}function setSiteDirectoryBasedOnURL(){var e=window.location.pathname;/^\/rural(\/.*)?$/.test(e)?(sessionStorage.setItem("sitedirectory","rural"),console.log("sitedirectory set to rural")):/^\/industrial(\/.*)?$/.test(e)&&(sessionStorage.setItem("sitedirectory","industrial"),console.log("sitedirectory set to industrial"))}document.addEventListener("DOMContentLoaded",()=>{checkRuralOrIndustrialHome(),setSiteDirectoryBasedOnURL()});