function checkRuralOrIndustrialHome(){var t=document.querySelectorAll(".page-template-home .btn--red");console.log(t),t.forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();var e=t.target.getAttribute("data-attr");sessionStorage.setItem("sitedirectory",e),console.log("sitedirectory set to: "+e),window.location.href=t.target.href})})}function desktopNavigationDrop(){document.querySelectorAll(".parent--link, .sub--link, .sub--sub--link").forEach(function(s){if(s.querySelector(".link-icon")){let e;s.addEventListener("click",function(t){t.preventDefault(),e&&clearTimeout(e),e=setTimeout(()=>{var t;s.classList.contains("parent--link")?(document.querySelectorAll(".sub--link--wrapper").forEach(function(t){t.classList.remove("active")}),(t=s.nextElementSibling)&&t.classList.contains("sub--link--wrapper")&&t.classList.add("active")):s.classList.contains("sub--link")&&(document.querySelectorAll(".sub--sub--parent--wrap").forEach(function(t){t.classList.remove("active")}),t=s.closest(".sub-link-item").querySelector(".sub--sub--parent--wrap"))&&t.classList.add("active")},200)}),s.addEventListener("dblclick",function(t){t.preventDefault(),s.classList.contains("parent--link")?(t=s.nextElementSibling)&&t.classList.contains("sub--link--wrapper")&&t.classList.remove("active"):s.classList.contains("sub--link")&&(t=s.closest(".sub-link-item").querySelector(".sub--sub--parent--wrap"))&&t.classList.remove("active")})}})}function switchNav(){document.querySelectorAll(".switch-button").forEach(function(t){t.addEventListener("click",function(){let e=this.getAttribute("data-attr");sessionStorage.setItem("sitedirectory",e),console.log("sitedirectory updated to:",sessionStorage.getItem("sitedirectory"));document.querySelectorAll(".navigation__menu").forEach(function(t){t.getAttribute("data-attr")===e?t.classList.add("active"):t.classList.remove("active")});var t=this.firstChild;"industrial"===e?t.nodeValue="Switch to Rural Structures ":t.nodeValue="Switch to Industrial Structures "})}),document.querySelectorAll(".sub--link").forEach(function(t){t.addEventListener("click",function(t){t.preventDefault();document.querySelectorAll(".sub--sub--parent--wrap").forEach(function(t){t.classList.remove("active")});t=this.closest(".sub--sub--parent--wrap");t&&t.classList.add("active")})})}function checkSiteDirectory(){var t;"rural"===sessionStorage.getItem("sitedirectory")?(t=document.querySelector('nav[data-attr="rural"]'))&&t.classList.add("active"):(t=document.querySelector('nav[data-attr="industrial"]'))&&t.classList.add("active")}function setSiteDirectoryBasedOnURL(){var t=window.location.pathname;/^\/rural(\/.*)?$/.test(t)?(sessionStorage.setItem("sitedirectory","rural"),console.log("sitedirectory set to rural")):/^\/industrial(\/.*)?$/.test(t)&&(sessionStorage.setItem("sitedirectory","industrial"),console.log("sitedirectory set to industrial"))}document.addEventListener("DOMContentLoaded",()=>{checkRuralOrIndustrialHome(),setSiteDirectoryBasedOnURL(),desktopNavigationDrop(),checkSiteDirectory(),switchNav()});